// Get the AI output (all in one text)
const aiOutput = $input.first().json.output;

// Step 1: Extract email (To)
const emailMatch = aiOutput.match(/\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}\b/);
const email = emailMatch ? emailMatch[0] : "NOT PROVIDED";

// Step 2: Extract Subject
let subjectMatch = aiOutput.match(/Subject[:\-]\s*(.*)/i);
let subject;
if (subjectMatch) {
    subject = subjectMatch[1].trim();
} else {
    // fallback: take first 5-7 words as subject
    subject = aiOutput.split(/\s+/).slice(0, 7).join(' ') + '...';
}

// Step 3: Extract Message
// Remove email, subject, and prefixes like "To:", "Message:" from the text
let message = aiOutput
    .replace(email, '')
    .replace(subjectMatch ? subjectMatch[0] : '', '')
    .replace(/To:\s*/gi, '')
    .replace(/Message:\s*/gi, '')
    .trim();

// Step 4: Clean Markdown in message
message = message
    .replace(/\*\*(.*?)\*\*/g, '$1')   // Remove bold
    .replace(/\*(.*?)\*/g, '$1')       // Remove italics / bullets temporarily
    .replace(/\\n/g, '\n')             // Convert literal \n to actual line breaks
    .replace(/^---$/gm, '')            // Remove horizontal rules
    .replace(/^\s*\*\s*/gm, '- ');     // Replace bullet asterisks with hyphens

// Return separated output
return [
  {
    json: {
      To: email,
      Subject: subject,
      Message: message
    }
  }
];

