const textOutput = $input.first().json.output;

const extractedData = {
  email_to: '',
  subject: '',
  message: ''
};

// Split the text into an array of lines
const lines = textOutput.split('\n');

let isMessage = false;

for (const line of lines) {
  // Check if we are at the start of the message
  if (line.trim().startsWith('**Message:**')) {
    isMessage = true;
    // Start collecting the message content from this line
    extractedData.message += line.replace(/\*|Message:/g, '').trim() + '\n';
    continue;
  }

  // If we are already in the message body, append the line
  if (isMessage) {
    extractedData.message += line.trim() + '\n';
  }

  // Handle other fields
  const cleanLine = line.replace(/\*/g, '').trim();

  if (cleanLine.startsWith('To:')) {
    extractedData.email_to = cleanLine.replace('To:', '').trim();
  } else if (cleanLine.startsWith('Subject:')) {
    extractedData.subject = cleanLine.replace('Subject:', '').trim();
  }
}

// Clean up any leading/trailing whitespace and newlines from the message
extractedData.message = extractedData.message.trim();

// Return updated JSON
const outputItem = $input.first();
outputItem.json.email_to = extractedData.email_to;
outputItem.json.subject = extractedData.subject;
outputItem.json.message = extractedData.message;

return $input.all();

