const data = $input.first().json;
const files = Array.isArray(data.files) ? data.files : [];

// --- helpers ---
function pad(n){ return String(n).padStart(2,'0'); }

// normalize date formats into YYYY-MM-DD
function normalizeDate(dateStr) {
  if (!dateStr) return null;

  // Already in YYYY-MM-DD
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateStr)) return dateStr;

  // Try parsing human-readable like "8 October 2025"
  const d = new Date(dateStr);
  if (!isNaN(d)) {
    const yyyy = d.getFullYear();
    const mm = String(d.getMonth() + 1).padStart(2, '0');
    const dd = String(d.getDate()).padStart(2, '0');
    return `${yyyy}-${mm}-${dd}`;
  }

  throw new Error(`Invalid date format: "${dateStr}"`);
}

// parse flexible time formats into [hh,mm,ss]
function parseTimeParts(raw) {
  if (!raw) throw new Error('Empty time string');
  const s = raw.trim();

  // hh:mm am/pm  (with optional space)
  let m = s.match(/^(\d{1,2}):(\d{2})\s*([AaPp][Mm])$/);
  if (m) {
    let hh = parseInt(m[1],10), mm = parseInt(m[2],10);
    const ampm = m[3].toUpperCase();
    if (ampm === 'PM' && hh !== 12) hh += 12;
    if (ampm === 'AM' && hh === 12) hh = 0;
    return [hh, mm, 0];
  }

  // hh:mm  (24-hour)
  m = s.match(/^(\d{1,2}):(\d{2})$/);
  if (m) return [parseInt(m[1],10), parseInt(m[2],10), 0];

  // hh am/pm  (no minutes)
  m = s.match(/^(\d{1,2})\s*([AaPp][Mm])$/);
  if (m) {
    let hh = parseInt(m[1],10);
    const ampm = m[2].toUpperCase();
    if (ampm === 'PM' && hh !== 12) hh += 12;
    if (ampm === 'AM' && hh === 12) hh = 0;
    return [hh, 0, 0];
  }

  // compact "105pm" (no colon)
  m = s.match(/^(\d{1,2})(\d{2})$/);
  if (m) return [parseInt(m[1],10), parseInt(m[2],10), 0];

  throw new Error(`Invalid time format: "${raw}"`);
}

// if startRaw contains "start - end" and endRaw missing, split it
function extractStartEnd(startRaw, endRaw) {
  if (!startRaw && !endRaw) return [null, null];
  if (startRaw && !endRaw && /[-â€“â€”]/.test(startRaw)) {
    const parts = startRaw.split(/[-â€“â€”]/).map(p => p.trim()).filter(Boolean);
    return [ parts[0] || null, parts[1] || null ];
  }
  return [ startRaw ? startRaw.trim() : null, endRaw ? endRaw.trim() : null ];
}

// build dateTime object for Google Calendar
function buildDateTimeObj(dateStr, timeStr) {
  dateStr = normalizeDate(dateStr);
  const [year, month, day] = dateStr.split('-').map(n => parseInt(n,10));
  if (![year,month,day].every(Number.isFinite)) throw new Error(`Invalid date "${dateStr}"`);
  const [hh, mm/*,ss*/] = parseTimeParts(timeStr);
  return {
    dateTime: `${pad(year)}-${pad(month)}-${pad(day)}T${pad(hh)}:${pad(mm)}:00`,
    timeZone: "America/Halifax"
  };
}

// align a base date forward to the next occurrence of targetDay
function alignDateToDay(baseDateStr, targetDay) {
  baseDateStr = normalizeDate(baseDateStr);
  const map = {
    sunday: 0, monday: 1, tuesday: 2, wednesday: 3,
    thursday: 4, friday: 5, saturday: 6
  };
  const target = map[String(targetDay || '').toLowerCase()];
  if (target === undefined) return baseDateStr;
  const d = new Date(baseDateStr + 'T00:00:00');
  let attempts = 0;
  while (d.getDay() !== target && attempts < 10) { d.setDate(d.getDate() + 1); attempts++; }
  return d.toISOString().slice(0,10);
}

// parse file.day field into BYDAY codes (MO,TU,...)
function getByDayCodes(dayField) {
  if (!dayField) return '';
  let days = [];
  if (Array.isArray(dayField)) days = dayField;
  else {
    days = String(dayField).split(/[,;\/]|(?:\band\b)|(?:&)/i).map(s => s.trim()).filter(Boolean);
    if (days.length === 1 && /\s+/.test(days[0])) days = days[0].split(/\s+/).map(s=>s.trim()).filter(Boolean);
  }
  const map = { monday:'MO', tuesday:'TU', wednesday:'WE', thursday:'TH', friday:'FR', saturday:'SA', sunday:'SU' };
  const codes = days.map(d => map[String(d).toLowerCase()]).filter(Boolean);
  return codes.join(',');
}

// add hours to a date string
function addHoursToDateTime(dateStr, timeParts, hoursToAdd) {
  dateStr = normalizeDate(dateStr);
  const [y,m,d] = dateStr.split('-').map(n=>parseInt(n,10));
  const [hh,mm,ss] = timeParts;
  const dt = new Date(y, m-1, d, hh, mm, ss);
  dt.setHours(dt.getHours() + hoursToAdd);
  return `${dt.getFullYear()}-${pad(dt.getMonth()+1)}-${pad(dt.getDate())}T${pad(dt.getHours())}:${pad(dt.getMinutes())}:00`;
}

// --- main ---
const events = [];

for (const file of files) {
  try {
    let rawStart = file.start_time || file.start || file.time || file.time_range || file.timeRange || '';
    let rawEnd   = file.end_time   || file.end   || '';

    [rawStart, rawEnd] = extractStartEnd(rawStart, rawEnd);
    if (!rawStart) throw new Error('Missing start time for file');

    let baseDate = normalizeDate(data.start_date_recurring || data.start_date || new Date().toISOString().slice(0,10));
    if (file.day) baseDate = alignDateToDay(baseDate, file.day);

    if (!rawEnd) {
      const startParts = parseTimeParts(rawStart);
      const endIso = addHoursToDateTime(baseDate, startParts, 1);
      rawEnd = endIso.split("T")[1].slice(0,5); // "HH:MM"
    }

    const startObj = buildDateTimeObj(baseDate, rawStart);
    const endObj   = buildDateTimeObj(baseDate, rawEnd);

    const event = {
      summary: file.summary || file.course || '',
      description: `Class Number: ${file.class_number || ''}`,
      location: file.location || '',
      start: startObj,
      end: endObj,
      recurrence_frequency: $input.first().json.recurrence_frequency,
      end_date_recurring: $input.first().json.end_date_recurring,
      start_date_recurring: $input.first().json.start_date_recurring
    };

    if (data.isRecurring && data.end_date_recurring) {
      const until = normalizeDate(data.end_date_recurring).replace(/-/g,'');
      const byday = getByDayCodes(file.day);
      const freq = (data.recurrence_frequency || 'WEEKLY').toUpperCase();
      const rule = byday ? `RRULE:FREQ=${freq};BYDAY=${byday};UNTIL=${until}T235959Z`
                         : `RRULE:FREQ=${freq};UNTIL=${until}T235959Z`;
      event.recurrence = [ rule ];
    }

    console.log('Event ready:', event.summary, event.start.dateTime, '->', event.end.dateTime, 'BYDAY=', file.day);
    events.push({ json: event });

  } catch (err) {
    events.push({ json: { error: err.message, file } });
  }
}

return events;

