const items = $input.all();

// Target date and class from Switch2
const targetDate = $('Switch2').first().json.date;
const targetClass = ($('Switch2').first().json.class_number || "");

// Normalize strings (remove spaces/dashes, lowercase)
const normalize = str => str.replace(/\s|-/g, '').toLowerCase();
const normalizedTargetClass = normalize(targetClass);

let output = [];

for (const item of items) {
  const event = item.json;

  // Extract event date (timed, all-day, or recurring)
  let eventDate = null;

  if (event.start) {
    if ('dateTime' in event.start && event.start.dateTime) {
      eventDate = event.start.dateTime.split("T")[0];
    } else if ('date' in event.start && event.start.date) {
      eventDate = event.start.date;
    }
  } else if (event.originalStartTime?.dateTime) {
    eventDate = event.originalStartTime.dateTime.split("T")[0];
  }

  // Normalize summary and description
  const normalizedSummary = normalize(event.summary || "");
  const normalizedDescription = normalize(event.description || "");

  // Check if class matches
  const classMatches = normalizedSummary.includes(normalizedTargetClass) || normalizedDescription.includes(normalizedTargetClass);

  // Check if date matches
  const dateMatches = eventDate === targetDate;

  // Push matching events
  if (classMatches && dateMatches) {
    output.push({ json: event });
  }
}

// If no matches, push a debug object instead
if (output.length === 0) {
  output.push({
    json: {
      message: "No matching events found",
      lookingFor: { targetDate, targetClass },
      checkedEvents: items.length
    }
  });
}

return output;

const subject = $input.first().json.Subject || "No Subject";
const from = $input.first().json.From || "Unknown Sender";
const snippet = $input.first().json.snippet|| "";

return [
  {
    json: {
      subject: subject,
      from: from,
      message: snippet
    }
  }
];
